function getGun(id)
{
	var currentGun = 0;
	findTheGun:
	for(var i in guns)
	{
		if(id == guns[i].id)
		{
			currentGun = guns[i];
			break findTheGun;
		}
	}
	if(currentGun == 0)
	{
		clientMessage("Error: no such gun");
		currentGun = AK47;
	}
	return currentGun;
}
